import React, { Component } from 'react';


class PropsInfo extends Component {
    render() {
        return (
            <div className="propsInfo">

                <h4>Props:</h4>
                <p>- When we need immutable data in our component, we can just add props to reactDOM.render() function in main.js and use it inside our component.</p>

                

            </div>
        )
    }
}

export default PropsInfo;